      out.write("\r\n");
      out.write("    // Hàm thêm bất động sản vào giỏ hàng\r\n");
      out.write("    function addToFavorites(id, title, price, area, imageUrl, address) {\r\n");
      out.write("        const existingProductIndex = cartItems.findIndex(item => item.id === id);\r\n");
      out.write("\r\n");
      out.write("        if (existingProductIndex !== -1) {\r\n");
      out.write("            alert(\"bất động sản này đã được quan tâm!\"); // Thông báo cho người dùng\r\n");
      out.write("        } else {\r\n");
      out.write("            const product = {\r\n");
      out.write("                id: id,\r\n");
      out.write("                title: title,\r\n");
      out.write("                price: parseFloat(price),\r\n");
      out.write("                area: area,\r\n");
      out.write("                imageUrl: imageUrl,\r\n");
      out.write("                address: address,\r\n");
      out.write("                quantity: 1 // Số lượng cố định là 1\r\n");
      out.write("            };\r\n");
      out.write("            cartItems.push(product);\r\n");
      out.write("            updateSessionStorage();\r\n");
      out.write("            updateCartDisplay();\r\n");
      out.write("            showMiniCart();\r\n");
      out.write("        }\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    // Cập nhật sessionStorage\r\n");
