      out.write("\r\n");
      out.write("        cartList.innerHTML = '';\r\n");
      out.write("\r\n");
      out.write("        if (cartItems.length === 0) {\r\n");
      out.write("            cartList.innerHTML = '<li>Bạn chưa có bất động sản đã lưu.</li>';\r\n");
      out.write("            itemCount.innerText = 0; // Cập nhật số lượng sản phẩm\r\n");
      out.write("            return; // Kết thúc hàm nếu giỏ hàng trống\r\n");
      out.write("        }\r\n");
      out.write("\r\n");
      out.write("        cartItems.forEach((item) => {\r\n");
      out.write("            const listItem = document.createElement('li');\r\n");
      out.write("            listItem.innerHTML = `\r\n");
      out.write("            <img src=\"${item.imageUrl}\" alt=\"${item.title}\" width=\"40\" height=\"40\">\r\n");
      out.write("            <div class=\"item-info\">\r\n");
      out.write("                <h4>${item.title}</h4>\r\n");
      out.write("                <p>Địa chỉ: ${item.address}</p>\r\n");
      out.write("                <p>Diện tích: ${item.area} m²</p>\r\n");
      out.write("                <span>Giá: ${item.price.toLocaleString()} tỷ</span>\r\n");
      out.write("\r\n");
      out.write("            </div>\r\n");
      out.write("            <button onclick=\"removeFromCart('${item.id}')\">Xóa</button>\r\n");
