      out.write("            cartList.appendChild(listItem);\r\n");
      out.write("        });\r\n");
      out.write("\r\n");
      out.write("        itemCount.innerText = cartItems.length; // Cập nhật số lượng sản phẩm\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
